import React from "react";
import "./style.css";
import "./App.css";
import { Card, CardText, CardTitle } from "reactstrap";
import { Container, Row, Col } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import NumberFormat from "react-number-format";

class Covid extends React.Component {
  render() {
    let data = this.props.data;

    return (
      <div>
        {/* <div>{data.NewConfirmed}</div>
        <div>{data.Recovered}</div> */}
        <Container>
          <Card>
            <CardTitle tag="h2">
              {" "}
              สถานการณ์ผู้ติดเชื้อ Covid-19 ในประเทศไทย
            </CardTitle>
            <CardTitle tag="h5">ข้อมูล ณ วันที่ {data.UpdateDate}</CardTitle>
            <Row>
              <Col sm="12" md={{ size: 12, offset: 0 }}>
                <Card body inverse color="Confirmed">
                  <CardTitle tag="h5">ติดเชื้อสะสม</CardTitle>
                  <CardText>
                    <h1>
                      <NumberFormat
                        value={data.Confirmed}
                        displayType={"text"}
                        thousandSeparator={true}
                      />
                    </h1>
                  </CardText>
                  <CardText tag="h5">
                    [ +
                    <NumberFormat
                      value={data.NewConfirmed}
                      displayType={"text"}
                      thousandSeparator={true}
                    />{" "}
                    ]
                  </CardText>
                </Card>
              </Col>
            </Row>
            <Row>
              <Col>
                <Card body inverse color="Recovered">
                  <CardTitle tag="h5">หายแล้ว</CardTitle>
                  <CardText tag="h1">
                    <NumberFormat
                      value={data.Recovered}
                      displayType={"text"}
                      thousandSeparator={true}
                    />
                  </CardText>
                  <CardText tag="h5">
                    [+{" "}
                    <NumberFormat
                      value={data.NewRecovered}
                      displayType={"text"}
                      thousandSeparator={true}
                    />{" "}
                    ]
                  </CardText>
                </Card>
              </Col>
              <Col>
                <Card body inverse color="Hospitalized">
                  <CardTitle tag="h5">รักษาอยู่ใน รพ.</CardTitle>
                  <CardText tag="h1">
                    <NumberFormat
                      value={data.Hospitalized}
                      displayType={"text"}
                      thousandSeparator={true}
                    />
                  </CardText>
                  <CardText tag="h5">
                    [+{" "}
                    <NumberFormat
                      value={data.NewHospitalized}
                      displayType={"text"}
                      thousandSeparator={true}
                    />{" "}
                    ]
                  </CardText>
                </Card>
              </Col>
              <Col>
                <Card body inverse color="Deaths">
                  <CardTitle tag="h5">เสียชีวิต</CardTitle>
                  <CardText tag="h1">
                    <NumberFormat
                      value={data.Deaths}
                      displayType={"text"}
                      thousandSeparator={true}
                    />
                  </CardText>
                  <CardText tag="h5">
                    <NumberFormat
                      value={data.NewDeaths}
                      displayType={"text"}
                      thousandSeparator={true}
                    />
                  </CardText>
                </Card>
              </Col>
            </Row>
          </Card>
        </Container>
      </div>
    );
  }
}
export default Covid;
